<?php/** * VideoController * * Controller untuk melakukan fungsi2 yang berkaitan dengan video * * @package Front Controller */class GalleryController extends Budpar_Controller_Common {    /**     * IS: -     * FS: -     * Desc: Fungsi inisialisasi     */    public function init() {        parent::init();    }    /**     * IS: Parameter page terdeklarasi     * FS: Mengirimkan ke viewer: video, itemPerPage, current, totalItems     * Desc: Fungsi untuk menampilkan daftar video     */    public function indexAction() {        $index = $this->getParam('index', 0);        if ($index > 0 && !$this->_request->isXmlHttpRequest()) {            throw new Zend_Controller_Action_Exception('This page does not exist', 404);        }        $search = $this->getParam('search');        $limit = 12;        $offset = $index * $limit;        $this->_helper->layout->setLayout('kebudayaan');        $db = new Admin_Model_DbTable_ImgGallery();        $records = $db->fetchAllWithLimit($search, $limit, $offset)->toArray();        $this->view->next = $index + 1;        $this->view->chunks = array_chunk($records, 4);        $this->view->langId = $this->_languageId;        $this->view->search = $search;        if ($this->_request->isXmlHttpRequest()) {            $this->_helper->layout->disableLayout();            $this->renderScript("gallery/list.phtml");        }    }}?>